import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;public class StringCalculatorTest {    @Test    public void Add_Nothing_Should_Return_Zero() {        int result = StringCalculator.add("");        Assertions.assertEquals(0, result);    }    @Test    public void Add_One_Number_Should_Return_The_Number() {        int result = StringCalculator.add("123");        Assertions.assertEquals(123, result);    }    @Test    public void Add_Two_Numbers_Should_Return_The_Sum_Of_Both() {        int result = StringCalculator.add("123,2");        Assertions.assertEquals(125, result);    }    @Test    public void Add_Three_Numbers_Should_Return_The_Sum_Of_Both() {        int result = StringCalculator.add("123,2,1000");        Assertions.assertEquals(1125, result);    }    @Test    public void Add_SeveralNumbers_Should_Return_The_Sum() {        StringBuilder builder = new StringBuilder();        int sum = 0;        for (int i = 0; i < 1000; i++) {            builder.append(i);            if (i != 1000) builder.append(",");            sum += i;        }        int result = StringCalculator.add(builder.toString());        Assertions.assertEquals(sum, result);    }    @Test    public void Add_Three_Numbers_Containing_Different_Separators() {        int result = StringCalculator.add("123,2\n1000");        Assertions.assertEquals(1125, result);    }    @Test    public void Having_Two_Delimiter_After_Each_Other_Should() {        int result = StringCalculator.add("123,2,\n");        Assertions.assertEquals(125, result);    }    @Test    public void Having_optional_delimiter() {        int result = StringCalculator.add(";\n123;2\n23");        Assertions.assertEquals(148, result);    }}